        // FunciÃ³n para obtener un post aleatorio
        function showLucky(root) {
            var feed = root.feed;
            var entries = feed.entry || [];
            var entry = entries[0]; // Obtener la primera entrada del feed
            if (entry) {
                for (var j = 0; j < entry.link.length; ++j) {
                    if (entry.link[j].rel == "alternate") {
                        window.location = entry.link[j].href; // Redireccionar al post aleatorio
                        break;
                    }
                }
            } else {
                console.log('No se encontraron entradas en el feed.');
            }
        }

        // FunciÃ³n para obtener un nÃºmero aleatorio y cargar el post correspondiente
        function fetchLuck(luck) {
            var script = document.createElement('script');
            script.type = 'text/javascript';
            script.src = blogUrl + '/feeds/posts/summary?start-index=' + luck + '&max-results=1&alt=json-in-script&callback=showLucky';
            document.getElementsByTagName('head')[0].appendChild(script);
        }

        // FunciÃ³n para obtener el nÃºmero total de entradas y seleccionar un Ã­ndice aleatorio
        function readLucky(root) {
            var feed = root.feed;
            var total = parseInt(feed.openSearch$totalResults.$t, 10);
            console.log('NÃºmero total de posts:', total); // Mostrar en consola el nÃºmero total de posts
            var luckyNumber = Math.floor(Math.random() * total) + 1; // Obtener un nÃºmero aleatorio entre 1 y total
            fetchLuck(luckyNumber);
        }

        // FunciÃ³n para iniciar el proceso de obtener un post aleatorio
        function feelingLucky() {
            var script = document.createElement('script');
            script.type = 'text/javascript';
            script.src = blogUrl + '/feeds/posts/summary?max-results=100&alt=json-in-script&callback=readLucky';
            document.getElementsByTagName('head')[0].appendChild(script);
        }

        // Detectar automÃ¡ticamente la URL del sitio
        var blogUrl = window.location.origin;
        var cacheKey = 'favoritosCache'; // Usando la clave 'favoritosCache' para la cachÃ©

        // Llamar a feelingLucky() solo en la pÃ¡gina de inicio y si no contiene /search?q= o /search/?q=
        document.addEventListener('DOMContentLoaded', function() {
            // Verificar si estamos en la pÃ¡gina de inicio y no contiene /search en la URL
            if ((window.location.pathname === '/' || window.location.pathname === '') &&
                !window.location.href.includes('/search')) {

                // Intentar obtener los datos de la cachÃ©
                var cachedData = localStorage.getItem(cacheKey);
                if (cachedData) {
                    try {
                        var cachedUrls = JSON.parse(cachedData);

                        // Verificar si hay etiquetas en los datos de la cachÃ©
                        var hasTags = cachedUrls.some(function(item) {
                            return item.etiquetas && item.etiquetas.length > 0;
                        });

                        if (hasTags) {
                            // Contar la frecuencia de las etiquetas
                            var tagCounts = {};
                            cachedUrls.forEach(function(item) {
                                if (item.etiquetas) {
                                    item.etiquetas.forEach(function(tag) {
                                        if (tagCounts[tag]) {
                                            tagCounts[tag]++;
                                        } else {
                                            tagCounts[tag] = 1;
                                        }
                                    });
                                }
                            });

                            // Convertir el objeto de conteo de etiquetas en un array y ordenarlo por frecuencia
                            var sortedTags = Object.keys(tagCounts).sort(function(a, b) {
                                return tagCounts[b] - tagCounts[a];
                            });

// Generar la URL con las etiquetas mÃ¡s repetidas primero
var query = sortedTags.map(function(tag) {
    // Verificar si el tag contiene un espacio
    if (tag.includes(' ')) {
        // Si contiene espacio, agregar comillas
        return 'label:"' + encodeURIComponent(tag) + '"';
    } else {
        // Si no contiene espacio, no agregar comillas
        return 'label:' + encodeURIComponent(tag);
    }
}).join('|');

var redirectUrl = blogUrl + '/search/?q=' + query + '&max-results=1';

                            // Redirigir a la URL generada
                            window.location = redirectUrl;
                        } else {
                            console.log('El cachÃ© no tiene etiquetas, llamando a feelingLucky().');
                            feelingLucky(); // Llamar a feelingLucky() si no hay etiquetas en la cachÃ©
                        }
                    } catch (error) {
                        console.error('Error al analizar los datos de la cachÃ©:', error);
                        feelingLucky(); // Si hay un error, llamar a feelingLucky() como respaldo
                    }
                } else {
                    console.log('No se encontrÃ³ cachÃ©, llamando a feelingLucky().');
                    feelingLucky(); // Si no hay datos en la cachÃ©, llamar a feelingLucky()
                }
            }
        });
