document.getElementById('search-form').addEventListener('submit', function(e) {
  e.preventDefault();
  var query = document.getElementById('search-input').value;
  if (query) {
    window.location.href = '/search?q=' + encodeURIComponent(query) + '&max-results=1';
  }
});
